import { StyleSheet } from 'react-native';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import { colors } from '../../styles';

export default StyleSheet.create({
    container: {
        flex: 1,
    },
    block: {
        marginTop: hp('31%'),
        justifyContent: 'center',
        alignItems: 'center',
    },
    image: {
        flex: 1,
        width: wp('100%'),
        height: hp('100%'),
    },
    shadow: {
        flex: 1,
        alignItems: 'center',
        width: wp('100%'),
        height: hp('100%'),
    },
    text: {
        color: colors.white,
        textAlign: 'center',
        marginTop: hp('32%'),
        marginBottom: 25
    },
    button: {
        borderWidth: 1,
        borderColor: colors.white,
        marginTop: 19
    },
    textButton: {
        fontWeight: '500',
        color: colors.white
    },
    link: {
        textDecorationLine: 'underline'
    }
});
